import React, { useState, useEffect } from "react";


export default function DataLoaderRow({student}){
    const [name, setName] = useState(student.name);
    const endPoint = "http://localhost:8080/students"
    const update = () => {
        fetch(endPoint, {
            method: 'PUT',
            headers: {
              'Content-Type': 'application/json'
            },
            body: JSON.stringify({ id : student.id, name: name})
        }).then(data => data.json())
    }
    return(
        <tr key={student.id} >
        <td className='delete-button' onClick={() => { 
          if (window.confirm('Are you sure you wish to delete this item?'))
          {
            fetch(endPoint + "/" + student.id, {
              method: 'DELETE',
              headers: {
                'Content-Type': 'application/json'
              }
          })
          }
          } }>{student.id}</td>
        <td id='name'><input value={name} onChange={(e)=>setName(e.target.value)}/><button onClick={() => update()}>update</button></td>
      </tr>  
    );
}
