import React, { useState} from "react";

import Modal from 'react-bootstrap/Modal'
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import $ from "jquery";


export default function ProductLoaderData({product}){
    
    const [show, setShow] = useState(false);

    const [productName, setProductName] = useState(product.productName);
    const [productType, setProductType] = useState(product.productType);
    const [productDescription, setProductDescription] = useState(product.productDescription);
    const [productPrice, setProductPrice] = useState(product.price);
    const [productQuantity, setProductQuantity] = useState(product.quantity);

    
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const endPoint = "http://localhost:808/products"

    return(
        
        <tr key={product.id}>
        
            <td>{product.id}</td>
        <td id='productName'>{productName}</td>
        <td id='productType'>{productType}</td>
        <td id='productPrice'>{productPrice}</td>
        <td id='productQuantity'>{productQuantity}</td>
        <td id='productDescription'>{productDescription} </td>
        
        
        
        <td>
            <div class="btn-group align-top">
            
                <Button class="btn btn-sm btn-outline-secondary" type="button" data-toggle="modal" data-target="#user-form-modal">Edit</Button>
                <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Product Form</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form noValidate validated={validated} onSubmit={handleSubmit}>

            <Form.Group className="mb-3" controlId="productName">
              <Form.Label>Product Name</Form.Label>
              <Form.Control required type="text" placeholder="Enter product name" onChange={e => setProductName(e.target.value)}/>
            </Form.Group>

            <Form.Group className="mb-3" controlId="productPrice">
              <Form.Label>Product Price</Form.Label>
              <Form.Control required type="number" placeholder="Enter product price"  onChange={e => setProductPrice(e.target.value)} />
            </Form.Group>

            <Form.Group className="mb-3" controlId="productQuantity">
              <Form.Label>Product Quantity</Form.Label>
              <Form.Control required type="number" placeholder="Enter product quantity"  onChange={e => setProductQuantity(e.target.value)}/>
            </Form.Group>

            <Form.Group className="mb-3" controlId="productDescription">
              <Form.Label>Product Description</Form.Label>
              <Form.Control required type="text" placeholder="Enter product description"  onChange={e => setProductDescription(e.target.value)}/>
            </Form.Group>

            <Form.Group className="mb-3" controlId="productType">
              <Form.Label>Product Type</Form.Label>
              {['radio'].map((type) => (
                <div key={`inline-${type}`} className="mb-3">
                  <Form.Check
                    defaultChecked="true"
                    onChange={e => setProductType("smart watch")}
                    inline
                    label="smart watch"
                    name="group1"
                    type={type}
                    id={`inline-${type}-1`}
                  />
                  <Form.Check
                    onChange={e => setProductType("phone")}
                    inline
                    label="phone"
                    name="group1"
                    type={type}
                    id={`inline-${type}-2`}
                  />
                  <Form.Check
                    inline
                    onChange={e => setProductType("cleaner")}
                    label="cleaner"
                    name="group1"
                    type={type}
                    id={`inline-${type}-3`}
                  />
                </div>
              ))}
            </Form.Group>

            <Button variant="primary" onClick={handleSubmit}>
              Submit
            </Button>
          </Form>
        </Modal.Body>
      </Modal>
                <button class="btn btn-sm btn-danger btn-outline-secondary" type="button" onClick={() => { 
          if (window.confirm('Are you sure you wish to DELETE this product?'))
          {
            fetch(endPoint + "/" + product.id, {
              method: 'DELETE',
              headers: {
                'Content-Type': 'application/json'
              }
          })
          }
          } }>Remove</button>
            </div>

            
          </td>

        
      </tr>  

    );
}
