import React, {useState} from 'react';
import './App.css';
import AddProduct from './NewProduct/AddProduct';
import Table from './TableData/Table';
import Modal from 'react-bootstrap/Modal'
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

function App() {
  const [show, setShow] = useState(false);

  const [productName, setProductName] = useState(product.productName);
  const [productType, setProductType] = useState(product.productType);
    const [productDescription, setProductDescription] = useState(product.productDescription);
    const [productPrice, setProductPrice] = useState(product.price);
    const [productQuantity, setProductQuantity] = useState(product.quantity);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
  return (
    <div className="App">
      <div class="jumbotron text-center bg-sky">
          <h2>Product Management</h2>
      </div>

      <Button onClick={handleShow}>
          Add New Product
        </Button>

        <Modal show={show} onHide={handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>Product Form</Modal.Title>
          </Modal.Header>
          <Modal.Body>
          <Form>

            <Form.Group className="mb-3" controlId="productName">
              <Form.Label>Product Name</Form.Label>
              <Form.Control type="text" placeholder="Enter product name" />
            </Form.Group>

            <Form.Group className="mb-3" controlId="productPrice">
              <Form.Label>Product Price</Form.Label>
              <Form.Control type="number" placeholder="Enter product price" />
            </Form.Group>

            <Form.Group className="mb-3" controlId="productQuantity">
              <Form.Label>Product Quantity</Form.Label>
              <Form.Control type="number" placeholder="Enter product quantity" />
            </Form.Group>

            <Form.Group className="mb-3" controlId="productDescription">
              <Form.Label>Product Description</Form.Label>
              <Form.Control type="number" placeholder="Enter product description" />
            </Form.Group>

            <Form.Group className="mb-3" controlId="productType">
                <Form.Label>Product Type</Form.Label>
                {['radio'].map((type) => (
                <div key={`inline-${type}`} className="mb-3">
                  <Form.Check
                    inline
                    label="smart watch"
                    name="group1"
                    type={type}
                    id={`inline-${type}-1`}
                  />
                  <Form.Check
                    inline
                    label="phone"
                    name="group1"
                    type={type}
                    id={`inline-${type}-2`}
                  />
                  <Form.Check
                    inline
                    label="cleaner"
                    name="group1"
                    type={type}
                    id={`inline-${type}-3`}
                  />
                </div>
              ))}
            </Form.Group>

            <Button variant="primary" type="submit">
              Submit
            </Button>
              </Form>
          </Modal.Body>
        </Modal>
      <Table/>


    </div>
  );
}

export default App;
