import React, {useState, useEffect } from 'react';
import DataLoader from "./DataLoader";

//Bootstrap and jQuery libraries



//Datatable Modules
import "datatables.net-dt/js/dataTables.dataTables"
import "datatables.net-dt/css/jquery.dataTables.min.css"
import $ from 'jquery'; 
import ModalProduct from './ModalProduct';
import ModalHeader from 'react-bootstrap/esm/ModalHeader';

export default function Table () {

 const [selectrow, setSelectRow] = useState(-1);
 const [data, setData] = useState([]);
 const endPoint = "http://localhost:808/products"
 useEffect(() => {
      //initialize datatable
    
    
   
    fetch(endPoint)
     .then(response => response.json())
     .then(data => 
      {
       setData(data); 
        $('#example').DataTable();
    });
  
 });
  
    //Datatable HTML
  return (
    <div className="MainDiv">
      
      
      <div className="container">
          
          <table id="example" class="display">
            <thead>
                <tr>
                    <th>ID </th>
                    <th>Product Name</th>
                    <th>Product Type</th>
                    <th>Product Price</th>
                    <th>Product Quantity</th>
                    <th>Product Description </th>
                    <th>UPDATE</th>
                </tr>
            </thead>
            <tbody>
            {data.map((el,i) => (<DataLoader product={el} onClick={()=>setSelectRow(el.id)}/>))}
            </tbody>
            <tfoot>
                <tr>
                    <th>ID </th>
                    <th>Product Name</th>
                    <th>Product Type</th>
                    <th>Product Price</th>
                    <th>Product Quantity</th>
                    <th>Product Description </th>
                    <th>UPDATE</th>
                </tr>
            </tfoot>
        </table>
        <h1>{selectrow}</h1>
        
        {data.map((el,i) => (<ModalProduct product={el} ishow={(el.id==selectrow)} Close={()=>setSelectRow(-1)}/>))}
        {data.map((el,i) => (<h1>{}</h1>))}
        </div>
      </div>
  );

}
