import React, { useState} from "react";

export default function ProductLoaderData({product}){
    

    const [productName, setProductName] = useState(product.productName);
    const [productType, setProductType] = useState(product.productType);
    const [productDescription, setProductDescription] = useState(product.productDescription);
    const [productPrice, setProductPrice] = useState(product.price);
    const [productQuantity, setProductQuantity] = useState(product.quantity);

    const endPoint = "http://localhost:808/products"

    return(
        
        <tr key={product.id}>
        
            <td>{product.id}</td>
        <td id='productName'>{productName}</td>
        <td id='productType'>{productType}</td>
        <td id='productPrice'><input type="number" value={productPrice} onChange={(e)=>setProductPrice(e.target.value)}/></td>
        <td id='productQuantity'><input type="number" value={productQuantity} onChange={(e)=>setProductQuantity(e.target.value)}/></td>
        <td id='productDescription'><input type="text" value={productDescription} onChange={(e)=>setProductDescription(e.target.value)}/></td>
        
        
        
        <td><button onClick={() => { 
          if (window.confirm('Are you sure you wish to UPDATE this product?'))
          {
            fetch(endPoint, {
                method: 'PUT',
                headers: {
                  'Content-Type': 'application/json'
                },
                body: JSON.stringify({ id : product.id, productName: productName, 
                    productType: productType,price: productPrice, quantity: productQuantity,
                     productDescription : productDescription})
            })
          }
          } }>update</button></td>

        <td><button className='delete-button' onClick={() => { 
          if (window.confirm('Are you sure you wish to DELETE this product?'))
          {
            fetch(endPoint + "/" + product.id, {
              method: 'DELETE',
              headers: {
                'Content-Type': 'application/json'
              }
          })
          }
          } }>X</button></td>
      </tr>  
    
    );
}
