import React, { useState} from "react";
import Modal from 'react-bootstrap/Modal'
import $ from "jquery";


export default function ProductLoaderData({product}){
    
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const [productName, setProductName] = useState(product.productName);
    const [productType, setProductType] = useState(product.productType);
    const [productDescription, setProductDescription] = useState(product.productDescription);
    const [productPrice, setProductPrice] = useState(product.price);
    const [productQuantity, setProductQuantity] = useState(product.quantity);

    const endPoint = "http://localhost:808/products"

    return(
        
        <tr key={product.id}>
        
            <td>{product.id}</td>
        <td id='productName'>{productName}</td>
        <td id='productType'>{productType}</td>
        <td id='productPrice'>{productPrice}</td>
        <td id='productQuantity'>{productQuantity}</td>
        <td id='productDescription'>{productDescription} </td>
        
        
        
        <td>
            <div class="btn-group align-top">
            
                <button class="btn btn-sm btn-outline-secondary" type="button" data-toggle="modal" data-target="#user-form-modal" onClick={handleShow}>Edit</button>
                <Modal show={show} onHide={handleClose}>
                    <Modal.Header closeButton>
                    <Modal.Title>Modal heading</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
                    <Modal.Footer>
                    <button variant="secondary" onClick={handleClose}>
                        Close
                    </button>
                    <Button variant="primary" onClick={handleClose}>
                        Save Changes
                    </Button>
                    </Modal.Footer>
                </Modal>
                
                <button class="btn btn-sm btn-danger btn-outline-secondary" type="button" onClick={() => { 
          if (window.confirm('Are you sure you wish to DELETE this product?'))
          {
            fetch(endPoint + "/" + product.id, {
              method: 'DELETE',
              headers: {
                'Content-Type': 'application/json'
              }
          })
          }
          } }>Remove</button>
            </div>

            
          </td>

        
      </tr>  

    );
}
